import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { Dimensions } from 'react-native';

const width = Dimensions.get('window').width;
const height = Dimensions.get('window').height;

const colors = ['red', 'green', 'blue', 'orange'];

export default function App() {
  const letters = [{id: 1,text:'A', show: false}, {id: 2,text:'B', show: false}, {id: 3,text:'C', show: false},{id: 4,text:'D', show: false},{id: 5,text:'E', show: false}, {id: 6,text:'F', show: false}, {id: 7,text:'G', show: false}, {id: 8,text:'H', show: false}];
  return (
    <View style={styles().container}>
      {letters.map((item) => <View key={item.id} style={styles().card}>

      <Text style={{...styles().text, opacity: item.show ? 1: 0}} >{item.text}</Text>
      </View>)}
      <StatusBar style="auto" />
    </View>
  );
}

const styles = () => StyleSheet.create({
  container: {
    display: 'flex',
    marginTop: 40,
    flexDirection: 'row', 
    flexWrap:'wrap'
  },
  card: {
   flexBasis: width /4.5,
   height: height / 4,
    backgroundColor: colors[1],
    justifyContent: 'center',
    alignItems: 'center',
    margin: 5
  },
  text: {
    fontSize: 50,
  }
});
