import React, { useState } from "react";
import { Dimensions, SafeAreaView, StyleSheet, Text, View } from "react-native";
import { TouchableOpacity } from "react-native-gesture-handler";
import getRandomChar from "../constants/characters";

const width = Dimensions.get("window").width;
const height = Dimensions.get("window").height;

const colors = ["red", "green", "blue", "orange"];
const maxCount = 2;

export default function App() {
  const [letters, setLetters] = useState(getRandomChar());
  const [matchIndexs, setMatchedIndexs] = useState([]);

  const onClick = (currentItem, index) => {
    let newD;
    if (matchIndexs.length < maxCount) {
      const newItems = [...letters];
      newItems[index].show = !newItems[index].show;
      newD = newItems;
    }

    if (matchIndexs.length === maxCount - 1) {
      const [oldIndex] = matchIndexs;
      if (letters[oldIndex].text === letters[index].text) {
        const newItems = [...letters];
        newItems[oldIndex].matched = true;
        newItems[index].matched = true;
        setLetters(newItems);
      }
      setTimeout(() => {
        const newItems = [...letters];
        if (!newItems[oldIndex].matched && !newItems[oldIndex].matched) {
          newItems[oldIndex].show = false;
          newItems[index].show = false;
        }

        newD = newItems;

        setLetters(newD);
        setMatchedIndexs([]);
      }, 500);
    }

    setLetters(newD);
    setMatchedIndexs(matchIndexs.concat(index));
  };

  const renderItem = (item, index) => {
    return (
      <TouchableOpacity
        key={item.id}
        onPress={() => !item.matched && onClick(item, index)}
      >
        <View style={styles.card}>
          <Text style={{ ...styles.text, opacity: item.show ? 1 : 0 }}>
            {item.text}
          </Text>
        </View>
      </TouchableOpacity>
    );
  };

  return (
    <SafeAreaView style={styles.container}>
      {letters.map(renderItem)}
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    display: "flex",
    marginTop: 20,
    flexDirection: "row",
    flexWrap: "wrap",
  },
  card: {
    width: width / 4.5,
    height: (height - 60) / 4,
    backgroundColor: "green",
    justifyContent: "center",
    alignItems: "center",
    margin: 5,
  },
  text: {
    fontSize: 50,
  },
});
