import React, { useState } from "react";
import { Dimensions, SafeAreaView, StyleSheet, Text, View } from "react-native";
import { TouchableOpacity } from "react-native-gesture-handler";
import getRandomChar from "../constants/characters";

const width = Dimensions.get("window").width;
const height = Dimensions.get("window").height;

const colors = ["red", "green", "blue", "orange"];
const maxCount = 2;

export default function App() {
  const [letters, setLetters] = useState(getRandomChar());
  const [matchIndexs, setMatchedIndexs] = useState([]);

  const onClick = (currentItem) => {
    let newD;
    if (clickCounter < maxCount) {
      newD = letters.map((inItem) => {
        if (currentItem.id == inItem.id) {
          inItem.show = !inItem.show;
        }
        return inItem;
      });
    }

    if (clickCounter === maxCount - 1) {
      setTimeout(() => {
        newD = letters.map((inItem) => {
          if (!inItem.matched) {
            inItem.show = false;
          }
          return inItem;
        });
        setLetters(newD);
        setMatchedIndexs(0);
      }, 500);
    }

    setLetters(newD);
    setMatchedIndexs(matchIndexs.concat());
  };

  return (
    <SafeAreaView style={styles.container}>
      {letters.map((item) => (
        <TouchableOpacity key={item.id} onPress={() => onClick(item)}>
          <View style={styles.card}>
            <Text style={{ ...styles.text, opacity: item.show ? 1 : 0 }}>
              {item.text}
            </Text>
          </View>
        </TouchableOpacity>
      ))}
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    display: "flex",
    marginTop: 20,
    flexDirection: "row",
    flexWrap: "wrap",
  },
  card: {
    width: width / 4.5,
    height: (height - 60) / 4,
    backgroundColor: "green",
    justifyContent: "center",
    alignItems: "center",
    margin: 5,
  },
  text: {
    fontSize: 50,
  },
});
